import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAppointments, createAppointment, updateAppointmentStatus, updateAppointment, deleteAppointment } from '../services/api';
import './AppointmentsAdmin.css';

const AppointmentsAdmin = () => {
  const navigate = useNavigate();
  const [appointments, setAppointments] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [phone, setPhone] = useState('');
  const [location, setLocation] = useState('');
  const [shoeCount, setShoeCount] = useState(1);
  const [appointmentDate, setAppointmentDate] = useState('');
  const [appointmentTime, setAppointmentTime] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [dateFilter, setDateFilter] = useState('');
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const res = await getAppointments();
      const sortedAppointments = res.data.sort((a, b) => {
        const statusOrder = { 'รอดำเนินการ': 1, 'ยืนยันแล้ว': 2, 'สำเร็จ': 3 };
        return statusOrder[a.status] - statusOrder[b.status] || new Date(b.appointment_date) - new Date(a.appointment_date);
      });
      setAppointments(sortedAppointments);
    } catch (error) {
      console.error("🔴 Error fetching appointments:", error);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบนัดหมายนี้?")) {
      try {
        await deleteAppointment(id);
        fetchAppointments();
      } catch (error) {
        console.error("🔴 Error deleting appointment:", error);
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
  };

  return (
    <div className="appointments-container">
      <h1>📅 รายการนัดหมาย</h1>
      <table>
        <thead>
          <tr>
            <th>ชื่อ</th>
            <th>เบอร์โทร</th>
            <th>สถานที่</th>
            <th>จำนวนรองเท้า</th>
            <th>วันที่</th>
            <th>เวลา</th>
            <th>สถานะ</th>
            <th>จัดการ</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map(appt => (
            <tr key={appt.id}>
              <td>{appt.customer_name}</td>
              <td>{appt.phone}</td>
              <td>{appt.location}</td>
              <td>{appt.shoe_count}</td>
              <td>{formatDate(appt.appointment_date)}</td>
              <td>{appt.appointment_time}</td>
              <td>{appt.status}</td>
              <td>
                {appt.status !== 'สำเร็จ' && <button onClick={() => handleEdit(appt)}>✏️ แก้ไข</button>}
                {appt.status !== 'สำเร็จ' && (
                  <button onClick={() => handleStatusUpdate(appt.id, appt.status === 'รอดำเนินการ' ? 'ยืนยันแล้ว' : 'สำเร็จ')}>
                    อัปเดต
                  </button>
                )}
                <button onClick={() => handleDelete(appt.id)}>🗑️ ลบ</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AppointmentsAdmin;
