import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAppointments, createAppointment, updateAppointmentStatus,deleteAppointment } from '../services/api';
import './AppointmentsAdmin.css';

const AppointmentsAdmin = () => {
    const navigate = useNavigate();
    const [appointments, setAppointments] = useState([]);
    const [customerName, setCustomerName] = useState('');
    const [phone, setPhone] = useState('');
    const [location, setLocation] = useState('');
    const [shoeCount, setShoeCount] = useState(1);
    const [appointmentDate, setAppointmentDate] = useState('');
    const [appointmentTime, setAppointmentTime] = useState('');
    const [statusFilter, setStatusFilter] = useState(['รอดำเนินการ', 'ยืนยันแล้ว']);
    const [editId, setEditId] = useState(null);
    const [filteredAppointments, setFilteredAppointments] = useState([]);

    useEffect(() => {
        fetchAppointments();
    }, []);

    useEffect(() => {
        setFilteredAppointments(appointments.filter(appt => statusFilter.includes(appt.status)));
    }, [statusFilter, appointments]);
        
  const fetchAppointments = async () => {
    try {
      const res = await getAppointments();
      const sortedAppointments = res.data.sort((a, b) => {
        const statusOrder = { 'รอดำเนินการ': 1, 'ยืนยันแล้ว': 2, 'สำเร็จ': 3 };
        return statusOrder[a.status] - statusOrder[b.status] || new Date(b.appointment_date) - new Date(a.appointment_date);
      });
      setAppointments(sortedAppointments);
    } catch (error) {
      console.error("🔴 Error fetching appointments:", error);
    }
  };

  const handleCreateAppointment = async () => {
    try {
      const newAppointment = {
        customer_name: customerName,
        phone,
        location,
        shoe_count: shoeCount,
        appointment_date: appointmentDate,
        appointment_time: appointmentTime,
      };
      await createAppointment(newAppointment);
      fetchAppointments();
      setCustomerName('');
      setPhone('');
      setLocation('');
      setShoeCount(1);
      setAppointmentDate('');
      setAppointmentTime('');
    } catch (error) {
      console.error("🔴 Error creating appointment:", error);
    }
  };

  const handleStatusUpdate = async (id, newStatus) => {
    try {
      await updateAppointmentStatus(id, newStatus);
      fetchAppointments();
    } catch (error) {
      console.error("🔴 Error updating status:", error);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบนัดหมายนี้?")) {
      try {
        await deleteAppointment(id);
        fetchAppointments();
      } catch (error) {
        console.error("🔴 Error deleting appointment:", error);
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
  };

  const handleStatusFilterChange = (status) => {
    setStatusFilter(prevFilters => 
        prevFilters.includes(status) 
            ? prevFilters.filter(item => item !== status) 
            : [...prevFilters, status]
    );
};

  return (
    <div className="appointments-container">
      <h1>📅 รายการนัดหมาย</h1>
      <button onClick={() => navigate('/dashboard')}>⬅️ กลับไปหน้า Dashboard</button>
      
      <div className="filters">
        <label>
            <input type="checkbox" checked={statusFilter.includes('รอดำเนินการ')} onChange={() => handleStatusFilterChange('รอดำเนินการ')} /> รอดำเนินการ
        </label>
        <label>
            <input type="checkbox" checked={statusFilter.includes('ยืนยันแล้ว')} onChange={() => handleStatusFilterChange('ยืนยันแล้ว')} /> ยืนยันแล้ว
        </label>
        <label>
            <input type="checkbox" checked={statusFilter.includes('สำเร็จ')} onChange={() => handleStatusFilterChange('สำเร็จ')} /> สำเร็จ
        </label>
     </div>
      
      <table>
        <thead>
          <tr>
            <th>ชื่อ</th>
            <th>เบอร์โทร</th>
            <th>สถานที่</th>
            <th>จำนวนรองเท้า</th>
            <th>วันที่</th>
            <th>เวลา</th>
            <th>สถานะ</th>
            <th>อัปเดต</th>
            <th>ลบ</th>
          </tr>
        </thead>
        <tbody>
          {appointments
            .sort((b, a) => new Date(b.appointment_date) - new Date(a.appointment_date)) // ✅ เรียงจากวันที่ล่าสุดไปเก่าสุด
            .map(appt => (
            <tr key={appt.id}>
              <td>{appt.customer_name}</td>
              <td>{appt.phone}</td>
              <td>{appt.location}</td>
              <td>{appt.shoe_count}</td>
              <td>{formatDate(appt.appointment_date)}</td>                
              <td>{appt.appointment_time}</td>
              <td>{appt.status}</td>
              <td>
                {appt.status !== 'สำเร็จ' && (
                  <button onClick={() => handleStatusUpdate(appt.id, appt.status === 'รอดำเนินการ' ? 'ยืนยันแล้ว' : 'สำเร็จ')}>
                    อัปเดต
                  </button>
                )}
              </td>
              <td>
              {appt.status !== 'สำเร็จ' && (
                  <button onClick={() => handleDelete(appt.id)}>ลบ</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>➕ เพิ่มนัดหมาย</h2>
      <input type="text" placeholder="ชื่อ" value={customerName} onChange={(e) => setCustomerName(e.target.value)} />
      <input type="text" placeholder="เบอร์โทร" value={phone} onChange={(e) => setPhone(e.target.value)} />
      <input type="text" placeholder="สถานที่" value={location} onChange={(e) => setLocation(e.target.value)} />
      <input type="number" placeholder="จำนวนรองเท้า" value={shoeCount} onChange={(e) => setShoeCount(e.target.value)} />
      <input type="date" value={appointmentDate} onChange={(e) => setAppointmentDate(e.target.value)} />
      <input type="time" value={appointmentTime} onChange={(e) => setAppointmentTime(e.target.value)} />
      <button onClick={handleCreateAppointment}>บันทึกนัดหมาย</button>
    </div>
  );
};

export default AppointmentsAdmin;
