import "./Sidebar.css"; // ‚úÖ ‡πÉ‡∏ä‡πâ CSS ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏™‡∏î‡∏á/‡∏ã‡πà‡∏≠‡∏ô‡πÄ‡∏°‡∏ô‡∏π

const Sidebar = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [isSuperAdmin, setIsSuperAdmin] = useState(false);
    const [userName, setUserName] = useState("");
    const [role, setRole] = useState("");
    const [branchId, setBranchId] = useState(null);
    const [branchName, setBranchName] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (token) {
            try {
                const user = jwtDecode(token);
                setIsSuperAdmin(user.isSuperAdmin);
                setUserName(user.email || "‡πÑ‡∏°‡πà‡∏ó‡∏£‡∏≤‡∏ö‡∏ä‡∏∑‡πà‡∏≠");
                setRole(user.isSuperAdmin ? "SuperAdmin" : "Employee");

                if (!user.isSuperAdmin) {
                    setBranchId(user.branch_id); // ‚úÖ ‡∏Å‡∏≥‡∏´‡∏ô‡∏î branchId ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô Employee
                }
            } catch (error) {
                console.error("üî¥ Error decoding token:", error);
                localStorage.removeItem("token");
                navigate("/login");
            }
        } else {
            navigate("/login");
        }
    }, [navigate]);

    useEffect(() => {
        if (branchId) {
            fetchBranchName(branchId);
        }
    }, [branchId]); // ‚úÖ ‡πÉ‡∏ä‡πâ useEffect ‡πÉ‡∏´‡πâ‡∏î‡∏∂‡∏á branchName ‡πÄ‡∏°‡∏∑‡πà‡∏≠ branchId ‡∏°‡∏µ‡∏Ñ‡πà‡∏≤

    const fetchBranchName = async (branchId) => {
        try {
            const res = await getBranchesById(branchId);
            setBranchName(res.data.name || "‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•");
        } catch (error) {
            console.error("üî¥ Error fetching branch name:", error);
            setBranchName("‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•");
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("token");
        navigate("/login");
    };

    return (
        <div className={`sidebar-container ${isOpen ? "open" : ""}`}>
            {/* ‡∏õ‡∏∏‡πà‡∏° Toggle ‡πÄ‡∏°‡∏ô‡∏π */}
            <button className="menu-toggle" onClick={() => setIsOpen(!isOpen)}>
                {isOpen ? "‚ùå ‡∏õ‡∏¥‡∏î" : "‚ò∞ ‡πÄ‡∏°‡∏ô‡∏π"}
            </button>
            <br />

            {/* ‚úÖ ‡πÄ‡∏°‡∏ô‡∏π‡∏î‡πâ‡∏≤‡∏ô‡∏Ç‡πâ‡∏≤‡∏á */}
            <div className="sidebar">
                

                <h2>üîπ ‡πÄ‡∏°‡∏ô‡∏π‡∏´‡∏•‡∏±‡∏Å</h2>
                <ul>
                    <li><Link to="/Dashboard">üè† ‡∏´‡∏ô‡πâ‡∏≤‡∏´‡∏•‡∏±‡∏Å</Link></li>
                    <li><Link to="/queue">üìã ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏Ñ‡∏¥‡∏ß‡∏á‡∏≤‡∏ô</Link></li>
                    <li><Link to="/appointments">üìÖ ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏ô‡∏±‡∏î‡∏´‡∏°‡∏≤‡∏¢</Link></li>
                    <li><Link to="/payments">üí≥ ‡∏ä‡∏≥‡∏£‡∏∞‡πÄ‡∏á‡∏¥‡∏ô</Link></li>
                    <li><Link to="/payouts">üí∏ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏£‡∏≤‡∏¢‡∏à‡πà‡∏≤‡∏¢</Link></li>
                    <li><Link to="/services">üõ† ‡∏ö‡∏£‡∏¥‡∏Å‡∏≤‡∏£</Link></li>
                    <li><Link to="/reports">üìä ‡∏£‡∏≤‡∏¢‡∏á‡∏≤‡∏ô</Link></li>

                    {/* ‚úÖ ‡πÄ‡∏°‡∏ô‡∏π‡∏û‡∏¥‡πÄ‡∏®‡∏©‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö SuperAdmin ‡πÄ‡∏ó‡πà‡∏≤‡∏ô‡∏±‡πâ‡∏ô */}
                    {isSuperAdmin && (
                        <>
                            <li><Link to="/employee">üë• ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏û‡∏ô‡∏±‡∏Å‡∏á‡∏≤‡∏ô</Link></li>
                            <li><Link to="/branch">üè¢ ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏≤‡∏Ç‡∏≤</Link></li>
                            <li><Link to="/users">üßë‚Äçüíº ‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô</Link></li>
                        </>
                    )}
                </ul>
                <li>-----------------------------------------------</li>
                {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ */}
                <div className="user-info">
                    <h6>üë§ {userName}</h6>
                    <p>üìå ‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á: <strong>{role}</strong></p>
                    {!isSuperAdmin && <p>üè¢ ‡∏™‡∏≤‡∏Ç‡∏≤: <strong>{branchName}</strong></p>}
                </div>
                

                {/* ‚úÖ ‡∏õ‡∏∏‡πà‡∏°‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö */}
                <button className="logout-button" onClick={handleLogout}>
                    üö™ ‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö
                </button>
            </div>
        </div>
    );
};

