import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import { Link } from "react-router-dom";
import "./Sidebar.css"; // ✅ ใช้ CSS สำหรับแสดง/ซ่อนเมนู

const Sidebar = () => {
    const [isOpen, setIsOpen] = useState(false); // ✅ ควบคุมการแสดงผล Sidebar
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem("token"); // ลบ Token
        navigate("/login"); // Redirect ไปหน้า Login
    };
    return (
        <div className={`sidebar-container ${isOpen ? "open" : ""}`}>
            {/* ปุ่ม Toggle เมนู */}
            <button className="menu-toggle" onClick={() => setIsOpen(!isOpen)}>
                {isOpen ? "❌ ปิด" : "☰ เมนู"}
            </button>

            {/* ✅ เมนูด้านข้าง */}
            <div className="sidebar">
                <h2>🔹 เมนูหลัก</h2>
                <ul>
                    <li><Link to="/Dashboard">🏠 หน้าหลัก</Link></li>
                    <li><Link to="/queue">📋 จัดการคิวงาน</Link></li>
                    <li><Link to="/appointments">📦 จัดการนัดหมาย</Link></li>
                    <li><Link to="/payments">📦 ชำระเงิน</Link></li>
                    <li><Link to="/services">🛠 บริการ</Link></li>
                    <li><Link to="/reports">📊 รายงาน</Link></li>
                    <li><Link to="/employeeManagement">🤷‍♂️ จัดการพนักงาน</Link></li>
                    <li><Link to="/branch">🤷‍♂️ จัดการสาขา</Link></li>
                    <li><Link to="/users">🤷‍♂️ ผู้ใช้งาน</Link></li>
                    <li><Link to="/settings">⚙️ ตั้งค่า</Link></li>
                </ul>
                <button className="logout-button" onClick={handleLogout}>
                    🚪 ออกจากระบบ
                </button>
            </div>
        </div>
    );
};

export default Sidebar;
