import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAppointments, createAppointment, updateAppointmentStatus, updateAppointment } from '../services/api';
import './AppointmentsAdmin.css';

const AppointmentsAdmin = () => {
  const navigate = useNavigate();
  const [appointments, setAppointments] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [phone, setPhone] = useState('');
  const [location, setLocation] = useState('');
  const [shoeCount, setShoeCount] = useState(1);
  const [appointmentDate, setAppointmentDate] = useState('');
  const [appointmentTime, setAppointmentTime] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [dateFilter, setDateFilter] = useState('');
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const res = await getAppointments();
      const sortedAppointments = res.data.sort((a, b) => {
        const statusOrder = { 'รอดำเนินการ': 1, 'ยืนยันแล้ว': 2, 'สำเร็จ': 3 };
        return statusOrder[a.status] - statusOrder[b.status] || new Date(b.appointment_date) - new Date(a.appointment_date);
      });
      setAppointments(sortedAppointments);
    } catch (error) {
      console.error("🔴 Error fetching appointments:", error);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
  };

  const handleCreateOrUpdateAppointment = async () => {
    if (!customerName || !phone || !location || !shoeCount || !appointmentDate || !appointmentTime) {
      alert("กรุณากรอกข้อมูลให้ครบถ้วน");
      return;
    }

    try {
      const newAppointment = {
        customer_name: customerName,
        phone,
        location,
        shoe_count: shoeCount,
        appointment_date: appointmentDate,
        appointment_time: appointmentTime,
      };
      
      if (editId) {
        await updateAppointment(editId, newAppointment);
        setEditId(null);
      } else {
        await createAppointment(newAppointment);
      }
      fetchAppointments();
      setCustomerName('');
      setPhone('');
      setLocation('');
      setShoeCount(1);
      setAppointmentDate('');
      setAppointmentTime('');
    } catch (error) {
      console.error("🔴 Error saving appointment:", error);
    }
  };

  const handleEdit = (appointment) => {
    if (appointment.status === 'สำเร็จ') return;
    setEditId(appointment.id);
    setCustomerName(appointment.customer_name);
    setPhone(appointment.phone);
    setLocation(appointment.location);
    setShoeCount(appointment.shoe_count);
    setAppointmentDate(appointment.appointment_date ? new Date(appointment.appointment_date).toISOString().split('T')[0] : '');
    setAppointmentTime(appointment.appointment_time);
  };

  const handleStatusUpdate = async (id, newStatus) => {
    try {
      await updateAppointmentStatus(id, newStatus);
      fetchAppointments();
    } catch (error) {
      console.error("🔴 Error updating status:", error);
    }
  };

  return (
    <div className="appointments-container">
      <h1>📅 รายการนัดหมาย</h1>
      <div className="filters">
        <input type="date" value={dateFilter} onChange={(e) => setDateFilter(e.target.value)} />
        <select value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)}>
          <option value="">ทั้งหมด</option>
          <option value="รอดำเนินการ">รอดำเนินการ</option>
          <option value="ยืนยันแล้ว">ยืนยันแล้ว</option>
          <option value="สำเร็จ">สำเร็จ</option>
        </select>
      </div>
      <table>
        <thead>
          <tr>
            <th>ชื่อ</th>
            <th>เบอร์โทร</th>
            <th>สถานที่</th>
            <th>จำนวนรองเท้า</th>
            <th>วันที่</th>
            <th>เวลา</th>
            <th>สถานะ</th>
            <th>จัดการ</th>
          </tr>
        </thead>
        <tbody>
          {appointments.filter(appt => (!statusFilter || appt.status === statusFilter) && (!dateFilter || appt.appointment_date === dateFilter))
            .map(appt => (
              <tr key={appt.id}>
                <td>{appt.customer_name}</td>
                <td>{appt.phone}</td>
                <td>{appt.location}</td>
                <td>{appt.shoe_count}</td>
                <td>{formatDate(appt.appointment_date)}</td>
                <td>{appt.appointment_time}</td>
                <td>{appt.status}</td>
                <td>
                  {appt.status !== 'สำเร็จ' && <button onClick={() => handleEdit(appt)}>✏️ แก้ไข</button>}
                  {appt.status !== 'สำเร็จ' && (
                    <button onClick={() => handleStatusUpdate(appt.id, appt.status === 'รอดำเนินการ' ? 'ยืนยันแล้ว' : 'สำเร็จ')}>
                      อัปเดต
                    </button>
                  )}
                </td>
              </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AppointmentsAdmin;
