import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getQueueDetail, getServices, updateQueue, addQueueItems, deleteQueueItem, uploadAfterImages } from '../services/api';
import '../assets/css/bootstrap.min.css'; // ✅ ใช้ Bootstrap
import './QueueDetail.css';

import AddShoeModal from "../components/AddShoeModal";
import ShoeDetailModal from "../components/ShoeDetailModal";

const QueueDetail = () => {
    const navigate = useNavigate();
    const { queue_id } = useParams();
    const [queue, setQueue] = useState(null);
    const [services, setServices] = useState([]);
    const [selectedService, setSelectedService] = useState('');
    const Backend_URL = 'http://localhost:5000'; 

    // ✅ State สำหรับ Modal
    const [showAddShoeModal, setShowAddShoeModal] = useState(false);
    const [showUploadAfterModal, setShowUploadAfterModal] = useState(false);
    const [showShoeDetailModal, setShowShoeDetailModal] = useState(false);
    const [selectedShoe, setSelectedShoe] = useState(null);
    const [selectedItemId, setSelectedItemId] = useState(null);
    const [afterImages, setAfterImages] = useState({
        front: null, back: null, left: null, right: null, top: null, bottom: null
    });

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) navigate('/login'); 
        fetchQueueDetail();
        fetchServices();
    }, []);

    const fetchQueueDetail = async () => {
        try {
            const res = await getQueueDetail(queue_id);
            setQueue(res.data);
        } catch (error) {
            console.error("🔴 Error fetching queue detail:", error);
        }
    };

    const fetchServices = async () => {
        try {
            const res = await getServices();
            setServices(res.data);
        } catch (error) {
            console.error("🔴 Error fetching services:", error);
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '-';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

    const handleShowShoeDetail = (shoe) => {
        setSelectedShoe(shoe);
        setShowShoeDetailModal(true);
    };

    const handleRemoveShoe = async (queue_item_id) => {
        if (!window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบรองเท้านี้?")) return;
        try {
            await deleteQueueItem(queue_id, queue_item_id);
            alert("✅ ลบรองเท้าเรียบร้อย!");
            fetchQueueDetail();
        } catch (error) {
            console.error("🔴 Error deleting shoe:", error);
        }
    };

    if (!queue) return <p>⏳ กำลังโหลดข้อมูล...</p>;

    return (
        <div className="container mt-4">
            {/* ปุ่มกลับไปหน้าคิว */}
            <div className="d-flex justify-content-between mb-3">
                <button className="btn btn-secondary" onClick={() => navigate('/queue')}>
                    ⬅️ กลับไปหน้าคิว
                </button>
                <button className="btn btn-primary" onClick={() => setShowAddShoeModal(true)}>
                    ➕ เพิ่มรองเท้า
                </button>
            </div>

            {/* รายละเอียดคิว */}
            <div className="card p-3 shadow mb-4">
                <h4>🏷️ รายละเอียดคิว #{queue_id}</h4>
                <p>👤 ลูกค้า: <strong>{queue.customer_name}</strong></p>
                <p>📞 เบอร์โทร: {queue.phone}</p>
                <p>📍 สถานที่: {queue.location}</p>
                <p>📅 รับเข้า: {formatDate(queue.received_date)}</p>
                <p>🚚 กำหนดส่งคืน: {formatDate(queue.delivery_date)}</p>
            </div>

            {/* ตารางรองเท้า */}
            <h4>👟 รายการรองเท้า</h4>
            <table className="table table-bordered table-striped">
                <thead>
                    <tr>
                        <th>แบรนด์</th>
                        <th>รุ่น</th>
                        <th>สี</th>
                        <th>หมายเหตุ</th>
                        <th>BEFORE</th>
                        <th>AFTER</th>
                        <th>รายละเอียด</th>
                        <th>ลบ</th>
                    </tr>
                </thead>
                <tbody>
                    {queue.queue_items.map((item, index) => (
                        <tr key={index}>
                            <td>{item.brand}</td>
                            <td>{item.model}</td>
                            <td>{item.color}</td>
                            <td>{item.notes}</td>
                            <td>
                                {item.image_before_front && (
                                    <img src={`${Backend_URL}${item.image_before_front}`} alt={`shoe-${index}`} width="50" />
                                )}
                            </td>
                            <td>
                                {item.image_after_front ? (
                                    <img src={`${Backend_URL}${item.image_after_front}`} alt={`after-${index}`} width="50" />
                                ) : (
                                    <button className="btn btn-warning btn-sm" onClick={() => setShowUploadAfterModal(true)}>
                                        Upload
                                    </button>
                                )}
                            </td>
                            <td>
                                <button className="btn btn-info btn-sm" onClick={() => handleShowShoeDetail(item)}>
                                    รายละเอียด
                                </button>
                            </td>
                            <td>
                                <button className="btn btn-danger btn-sm" onClick={() => handleRemoveShoe(item.queue_item_id)}>
                                    ❌
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Modal เพิ่มรองเท้า */}
            {showAddShoeModal && (
                <AddShoeModal 
                    show={showAddShoeModal} 
                    onClose={() => setShowAddShoeModal(false)} 
                    services={services} 
                    onAddShoe={fetchQueueDetail} 
                />
            )}

            {/* Modal แสดงรายละเอียดรองเท้า */}
            {showShoeDetailModal && (
                <ShoeDetailModal 
                    show={showShoeDetailModal} 
                    onClose={() => setShowShoeDetailModal(false)} 
                    shoe={selectedShoe} 
                />
            )}
        </div>
    );
};

export default QueueDetail;
