            const user = jwtDecode(token);
            if (!user.isSuperAdmin) {
                navigate("/dashboard"); // ‚ùå ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡πÉ‡∏ä‡πà SuperAdmin ‡πÉ‡∏´‡πâ Redirect ‡∏≠‡∏≠‡∏Å‡πÑ‡∏õ
                return;
            }
            setIsSuperAdmin(true);
            fetchBranches();
        } catch (error) {
            console.error("üî¥ Error decoding token:", error);
        }
    }, []);

    useEffect(() => {
        if (selectedBranch) {
            fetchEmployees();
        }
    }, [selectedBranch]);

    const fetchBranches = async () => {
        try {
            const res = await getBranches();
            setBranches(res.data);
        } catch (error) {
            console.error("üî¥ Error fetching branches:", error);
        }
    };

    const fetchEmployees = async () => {
        try {
            const res = await getEmployees(selectedBranch);
            setEmployees(res.data);
        } catch (error) {
            console.error("üî¥ Error fetching employees:", error);
        }
    };

    const handleCreateOrUpdateEmployee = async (employeeData) => {
        try {
            if (!employeeData.name || !employeeData.email || !employeeData.phone || !employeeData.role || !employeeData.branch_id) {
                alert("‚ùå ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô");
                return;
            }

            let response;
            if (employeeData.id) {
                response = await updateEmployee(employeeData.id, employeeData);
            } else {
                response = await createEmployee(employeeData);
            }

            if (response.status === 201 || response.status === 200) {
                alert("‚úÖ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à!");
                fetchEmployees();
                setShowAddModal(false);
                setEditEmployee(null);
            } else {
                alert("‚ùå ‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏î‡πâ");
            }
        } catch (error) {
            console.error("üî¥ Error saving employee:", error);
            alert("‚ùå ‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î ‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏û‡∏ô‡∏±‡∏Å‡∏á‡∏≤‡∏ô‡πÑ‡∏î‡πâ");
