import React, { useState } from "react";
import { uploadAfterImages } from "../services/api"; // ‚úÖ Import ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î
import "../assets/css/bootstrap.min.css";

const ShoeDetailModal = ({ show, onClose, shoe, queue_id, fetchQueueDetail }) => {
    if (!show || !shoe) return null;

    const Backend_URL = 'http://localhost:5000';
    const [updatedImages, setUpdatedImages] = useState({}); // ‚úÖ ‡πÄ‡∏Å‡πá‡∏ö‡∏£‡∏π‡∏õ‡∏ó‡∏µ‡πà‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡πÉ‡∏´‡∏°‡πà

    // ‚úÖ ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡∏Ñ‡∏•‡∏¥‡∏Å‡∏ó‡∏µ‡πà‡∏£‡∏π‡∏õ ‡πÉ‡∏´‡πâ‡πÄ‡∏õ‡∏¥‡∏î‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î
    const handleImageUpload = (position, event) => {
        const file = event.target.files[0];
        if (file) {
            setUpdatedImages(prev => ({
                ...prev,
                [position]: file
            }));
        }
    };

    // ‚úÖ ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û
    const handleSaveImages = async () => {
        if (Object.keys(updatedImages).length === 0) {
            alert("‚ùå ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏£‡∏π‡∏õ‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏Å‡πà‡∏≠‡∏ô‡∏Å‡∏î‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å!");
            return;
        }

        const formData = new FormData();
        Object.keys(updatedImages).forEach(position => {
            formData.append(`image_after_${position}`, updatedImages[position]);
        });

        try {
            await uploadAfterImages(queue_id, shoe.queue_item_id, formData);
            alert("‚úÖ ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ After ‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à!");
            fetchQueueDetail(); // ‚úÖ ‡∏£‡∏µ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•
            onClose(); // ‚úÖ ‡∏õ‡∏¥‡∏î Modal
        } catch (error) {
            console.error("üî¥ Error uploading after images:", error);
            alert("‚ùå ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à!");
        }
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content card p-4 shadow" style={{ backgroundColor: "#fff" }}>
                <h2 className="text-center">üëü ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤</h2>
                
                {/* ‚úÖ ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏£‡∏≠‡∏á‡πÄ‡∏ó‡πâ‡∏≤ */}
                <div className="mb-3">
                    <p><strong>‡πÅ‡∏ö‡∏£‡∏ô‡∏î‡πå:</strong> {shoe.brand}</p>
                    <p><strong>‡∏£‡∏∏‡πà‡∏ô:</strong> {shoe.model}</p>
                    <p><strong>‡∏™‡∏µ:</strong> {shoe.color}</p>
                    <p><strong>‡∏´‡∏°‡∏≤‡∏¢‡πÄ‡∏´‡∏ï‡∏∏:</strong> {shoe.notes || "-"}</p>
                </div>

                {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏π‡∏õ Before & After ‡πÅ‡∏ö‡∏ö‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡πÑ‡∏î‡πâ */}
                <h3 className="text-center">üì∑ ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ After</h3>
                <table className="table table-bordered text-center">
                    <thead className="table-dark">
                        <tr>
                            <th>‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á</th>
                            <th>Before</th>
                            <th>After (‡∏Ñ‡∏•‡∏¥‡∏Å‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {["front", "back", "left", "right", "top", "bottom"].map((position) => (
                            <tr key={position}>
                                <td><strong>{position.toUpperCase()}</strong></td>
                                <td>
                                    {shoe[`image_before_${position}`] ? (
                                        <img src={`${Backend_URL}${shoe[`image_before_${position}`]}`} 
                                             alt={`before-${position}`} width="80" className="img-thumbnail" />
                                    ) : "-"}
                                </td>
                                <td>
                                    <label htmlFor={`upload-${position}`} style={{ cursor: "pointer" }}>
                                        <img src={
                                            updatedImages[position] 
                                                ? URL.createObjectURL(updatedImages[position]) 
                                                : (shoe[`image_after_${position}`] ? `${Backend_URL}${shoe[`image_after_${position}`]}` : "/placeholder.jpg")
                                        }
                                            alt={`after-${position}`} width="80" className="img-thumbnail"
                                        />
                                    </label>
                                    <input
                                        type="file"
                                        accept="image/*"
                                        id={`upload-${position}`}
                                        style={{ display: "none" }}
                                        onChange={(e) => handleImageUpload(position, e)}
                                    />
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                {/* ‚úÖ ‡∏õ‡∏∏‡πà‡∏°‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å & ‡∏õ‡∏¥‡∏î */}
                <div className="text-center mt-3">
                    <button className="btn btn-success me-2" onClick={handleSaveImages}>
                        ‚úÖ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å
                    </button>
                    <button className="btn btn-danger" onClick={onClose}>
                        ‚ùå ‡∏õ‡∏¥‡∏î
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ShoeDetailModal;
