import React, { useState } from "react";
import { uploadAfterImages } from "../services/api"; // ✅ Import ฟังก์ชันอัปโหลด
import "../assets/css/bootstrap.min.css";

const ShoeDetailModal = ({ show, onClose, shoe, queue_id, fetchQueueDetail }) => {
    if (!show || !shoe) return null;

    const Backend_URL = 'http://localhost:5000';
    const [updatedImages, setUpdatedImages] = useState({}); // ✅ เก็บรูปที่อัปโหลดใหม่

    // ✅ เมื่อคลิกที่รูป ให้เปิดตัวเลือกอัปโหลด
    const handleImageUpload = (position, event) => {
        const file = event.target.files[0];
        if (file) {
            setUpdatedImages(prev => ({
                ...prev,
                [position]: file
            }));
        }
    };

    // ✅ ฟังก์ชันอัปโหลดรูปภาพ
    const handleSaveImages = async () => {
        if (Object.keys(updatedImages).length === 0) {
            alert("❌ กรุณาเลือกรูปที่ต้องการอัปโหลดก่อนกดบันทึก!");
            return;
        }

        const formData = new FormData();
        Object.keys(updatedImages).forEach(position => {
            formData.append(`image_after_${position}`, updatedImages[position]);
        });

        try {
            await uploadAfterImages(queue_id, shoe.queue_item_id, formData);
            alert("✅ อัปโหลดรูป After สำเร็จ!");
            fetchQueueDetail(); // ✅ รีโหลดข้อมูล
            onClose(); // ✅ ปิด Modal
        } catch (error) {
            console.error("🔴 Error uploading after images:", error);
            alert("❌ อัปโหลดรูปไม่สำเร็จ!");
        }
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content card p-4 shadow" style={{ backgroundColor: "#fff" }}>
                <h2 className="text-center">👟 รายละเอียดรองเท้า</h2>
                
                {/* ✅ ข้อมูลรองเท้า */}
                <div className="mb-3">
                    <p><strong>แบรนด์:</strong> {shoe.brand}</p>
                    <p><strong>รุ่น:</strong> {shoe.model}</p>
                    <p><strong>สี:</strong> {shoe.color}</p>
                    <p><strong>หมายเหตุ:</strong> {shoe.notes || "-"}</p>
                </div>

                {/* ✅ แสดงรูป Before & After แบบอัปโหลดได้ */}
                <h3 className="text-center">📷 อัปโหลดรูป After</h3>
                <table className="table table-bordered text-center">
                    <thead className="table-dark">
                        <tr>
                            <th>ตำแหน่ง</th>
                            <th>Before</th>
                            <th>After (คลิกเพื่อเปลี่ยน)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {["front", "back", "left", "right", "top", "bottom"].map((position) => (
                            <tr key={position}>
                                <td><strong>{position.toUpperCase()}</strong></td>
                                <td>
                                    {shoe[`image_before_${position}`] ? (
                                        <img src={`${Backend_URL}${shoe[`image_before_${position}`]}`} 
                                             alt={`before-${position}`} width="80" className="img-thumbnail" />
                                    ) : "-"}
                                </td>
                                <td>
                                    <label htmlFor={`upload-${position}`} style={{ cursor: "pointer" }}>
                                        <img src={
                                            updatedImages[position] 
                                                ? URL.createObjectURL(updatedImages[position]) 
                                                : (shoe[`image_after_${position}`] ? `${Backend_URL}${shoe[`image_after_${position}`]}` : "/placeholder.jpg")
                                        }
                                            alt={`after-${position}`} width="80" className="img-thumbnail"
                                        />
                                    </label>
                                    <input
                                        type="file"
                                        accept="image/*"
                                        id={`upload-${position}`}
                                        style={{ display: "none" }}
                                        onChange={(e) => handleImageUpload(position, e)}
                                    />
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                {/* ✅ ปุ่มบันทึก & ปิด */}
                <div className="text-center mt-3">
                    <button className="btn btn-success me-2" onClick={handleSaveImages}>
                        ✅ บันทึก
                    </button>
                    <button className="btn btn-danger" onClick={onClose}>
                        ❌ ปิด
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ShoeDetailModal;
