import React, { useState } from "react";
import { processPayment } from "../services/api";
import "bootstrap/dist/css/bootstrap.min.css";

const PaymentModal = ({ queue, onClose }) => {
    const [additionalFees, setAdditionalFees] = useState([]);
    const [discount, setDiscount] = useState(0);
    const [paymentMethod, setPaymentMethod] = useState("");

    const handleAddFee = () => {
        setAdditionalFees([...additionalFees, { name: "", amount: "" }]);
    };

    const handleFeeChange = (index, field, value) => {
        const updatedFees = [...additionalFees];
        updatedFees[index][field] = value;
        setAdditionalFees(updatedFees);
    };

    const totalAdditionalFees = additionalFees.reduce((sum, fee) => sum + (Number(fee.amount) || 0), 0);
    const totalAmount = (Number(queue.total_price) + totalAdditionalFees - Number(discount)).toFixed(2);

    const handleConfirmPayment = async () => {
        if (!paymentMethod) {
            alert("❌ กรุณาเลือกวิธีชำระเงิน");
            return;
        }

        try {
            await processPayment(queue.id, totalAmount, additionalFees, paymentMethod, discount);
            alert("✅ ชำระเงินเรียบร้อย!");
            onClose();
        } catch (error) {
            console.error("🔴 Error processing payment:", error);
        }
    };

    return (
        <div className="modal-overlay">
            <div className="modal-dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">💳 ชำระเงินสำหรับ {queue.customer_name}</h5>
                        <button type="button" className="btn-close" onClick={onClose}></button>
                    </div>
                    <div className="modal-body">
                        <p><strong>จำนวนคู่:</strong> {queue.total_pairs}</p>
                        <p><strong>ยอดรวม:</strong> {queue.total_price} บาท</p>

                        {/* ค่าใช้จ่ายเพิ่มเติม */}
                        <h6 className="mt-3">💸 ค่าใช้จ่ายเพิ่มเติม</h6>
                        {additionalFees.map((fee, index) => (
                            <div key={index} className="row g-2">
                                <div className="col-6">
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="ระบุค่าใช้จ่าย"
                                        value={fee.name}
                                        onChange={(e) => handleFeeChange(index, "name", e.target.value)}
                                    />
                                </div>
                                <div className="col-4">
                                    <input
                                        type="number"
                                        className="form-control"
                                        placeholder="จำนวนเงิน"
                                        value={fee.amount}
                                        onChange={(e) => handleFeeChange(index, "amount", e.target.value)}
                                    />
                                </div>
                                <div className="col-2">
                                    <button className="btn btn-danger" onClick={() => {
                                        setAdditionalFees(additionalFees.filter((_, i) => i !== index));
                                    }}>❌</button>
                                </div>
                            </div>
                        ))}
                        <button className="btn btn-secondary mt-2" onClick={handleAddFee}>➕ เพิ่มค่าใช้จ่าย</button>

                        {/* ส่วนลด */}
                        <h6 className="mt-3">🎁 ส่วนลด</h6>
                        <input
                            type="number"
                            className="form-control"
                            placeholder="ระบุจำนวนเงินส่วนลด"
                            value={discount}
                            onChange={(e) => setDiscount(e.target.value)}
                        />

                        {/* ยอดรวมทั้งหมด */}
                        <h4 className="mt-4 text-center text-success">
                            💰 รวมทั้งสิ้น: {totalAmount} บาท
                        </h4>

                        {/* เลือกวิธีชำระเงิน */}
                        <h6 className="mt-3">เลือกวิธีชำระเงิน</h6>
                        <select className="form-select" value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                            <option value="">เลือกวิธีชำระเงิน</option>
                            <option value="เงินสด">💵 เงินสด</option>
                            <option value="QR Code">📲 QR Code</option>
                        </select>
                    </div>

                    <div className="modal-footer">
                        <button className="btn btn-danger" onClick={onClose}>❌ ปิด</button>
                        <button className="btn btn-success" onClick={handleConfirmPayment}>✅ ยืนยันชำระเงิน</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PaymentModal;
