import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getQueue, getBranches, updateQueueStatus, deleteQueue } from '../services/api';
import { jwtDecode } from "jwt-decode";
import './QueueAdmin.css';
import { generateInvoice } from '../services/api';

const QueueAdmin = () => {
    const navigate = useNavigate();
    const [queue, setQueue] = useState([]);
    const [branches, setBranches] = useState([]);
    const [selectedBranch, setSelectedBranch] = useState(null);
    const [statusFilter, setStatusFilter] = useState(['รับเข้า', 'อยู่ระหว่างทำความสะอาด', 'เตรียมส่ง', 'กำลังจัดส่ง']); // set default ให้ status
    const [filteredQueue, setFilteredQueue] = useState([]);
    const [invoiceUrl, setInvoiceUrl] = useState(null);
    const [showInvoiceModal, setShowInvoiceModal] = useState(false);
    const [isSuperAdmin, setIsSuperAdmin] = useState(false);

    const Backend_URL = 'http://localhost:5000';

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        try {
            const user = jwtDecode(token);
            setIsSuperAdmin(user.isSuperAdmin);
            setSelectedBranch(user.isSuperAdmin ? null : user.branch_id); 

            if (user.isSuperAdmin) {
                fetchBranches();
            } else {
                fetchQueue(user.branch_id);
            }

        } catch (error) {
            console.error("🔴 Error decoding token:", error);
            navigate("/login");
        }
    }, []);

    useEffect(() => {
        if (selectedBranch !== null) {
            fetchQueue(selectedBranch);
        }
    }, [selectedBranch]);

    const fetchBranches = async () => {
        try {
            const res = await getBranches();
            setBranches(res.data);
        } catch (error) {
            console.error("🔴 Error fetching branches:", error);
        }
    };

    const fetchQueue = async (branchId) => {
        try {
            console.log("📌 Debug: Fetching queue for branchId =", branchId);
            const res = await getQueue(branchId);
            setQueue(res.data);
        } catch (error) {
            console.error("🔴 Error fetching queue:", error);
        }
    };

    const handleBranchChange = (e) => {
        const branchId = e.target.value;
        setSelectedBranch(branchId);
    };

    const formatDate = (dateString) => {
        if (!dateString) return '-';
        const date = new Date(dateString);
        return `${date.getDate().toString().padStart(2, '0')}/${(date.getMonth() + 1).toString().padStart(2, '0')}/${date.getFullYear()}`;
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'รับเข้า': return 'bg-danger text-white';
            case 'อยู่ระหว่างทำความสะอาด': return 'bg-warning text-dark';
            case 'เตรียมส่ง': return 'bg-warning text-white';
            case 'กำลังจัดส่ง': return 'bg-primary text-dark';
            case 'จัดส่งสำเร็จ': return 'bg-success text-white';
            case 'ชำระเงินแล้ว': return 'bg-success text-white';
            default: return 'bg-light text-dark';
        }
    };

    return (
        <div className="queue-container">
            <h2>📋 ระบบคิวงาน</h2>
            
            {/* ✅ แสดง Dropdown ให้ SuperAdmin เลือกสาขา */}
            {isSuperAdmin && (
                <div className="mb-3">
                    <label className="form-label">เลือกสาขา</label>
                    <select className="form-control" value={selectedBranch || ''} onChange={handleBranchChange}>
                        <option value="">-- เลือกสาขา --</option>
                        {branches.map(branch => (
                            <option key={branch.id} value={branch.id}>{branch.name}</option>
                        ))}
                    </select>
                </div>
            )}

            <table className="table table-striped table-bordered table-hover">
                <thead className="table-dark">
                    <tr>
                        <th>ชื่อ</th>
                        <th>สถานที่</th>
                        <th>จำนวนคู่</th>
                        <th>จำนวนเงิน</th>
                        <th>วันที่รับเข้า</th>
                        <th>วันที่ส่งคืน</th>
                        <th>สถานะคิว</th>
                        <th>สถานะชำระเงิน</th>
                        <th>รายละเอียด</th>
                        <th>สร้างใบแจ้งราคา</th>
                    </tr>
                </thead>
                <tbody>
                    {queue.map(item => (  
                        <tr key={item.queue_id}>
                            <td>{item.customer_name}</td>
                            <td>{item.location}</td>
                            <td>{item.total_pairs}</td>
                            <td>{item.total_price}</td>
                            <td>{formatDate(item.received_date)}</td>
                            <td>{formatDate(item.delivery_date)}</td>
                            <td>
                                <span className={`badge ${getStatusColor(item.status)}`}>
                                    {item.status}
                                </span>
                            </td>
                            <td>
                                <span className={`badge ${getStatusColor(item.payment_status)}`}>
                                    {item.payment_status}
                                </span>
                            </td>
                            <td>
                                <button className="btn btn-info btn-sm" onClick={() => navigate(`/queue/${item.queue_id}/detail`)}>
                                    รายละเอียด
                                </button>
                            </td>
                            <td>
                                <button className="btn btn-success btn-sm" onClick={() => generateInvoice(item.queue_id)}>
                                    ใบแจ้งราคา
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* ✅ Modal แสดงใบแจ้งราคา */}
            {showInvoiceModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h2>📜 ใบแจ้งราคา</h2>
                        {invoiceUrl ? (
                            <img src={invoiceUrl} alt="Invoice" style={{ width: "100%", borderRadius: "10px" }} />
                        ) : (
                            <p>⏳ กำลังโหลดใบแจ้งราคา...</p>
                        )}
                        <button onClick={() => setShowInvoiceModal(false)}>❌ ปิด</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default QueueAdmin;
