import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { getBranches, createBranch, updateBranch, deleteBranch } from "../services/api";
import AddBranchModal from "../components/AddBranchModal";
import "./BranchManagement.css";
import "../assets/css/bootstrap.min.css";

const BrandManagement = () => {
    const navigate = useNavigate();
    const [branches, setBranches] = useState([]);
    const [showAddModal, setShowAddModal] = useState(false);
    const [editBranch, setEditBranch] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/login");
        }
        fetchBranches();
    }, []);

    const fetchBranches = async () => {
        try {
            const res = await getBranches();
            setBranches(res.data);
        } catch (error) {
            console.error("🔴 Error fetching branches:", error);
        }
    };

    const handleCreateOrUpdateBranch = async (branchData) => {
        try {
            if (branchData.id) {
                await updateBranch(branchData.id, branchData);
            } else {
                await createBranch(branchData);
            }
            fetchBranches();
            setShowAddModal(false);
            setEditBranch(null);
        } catch (error) {
            console.error("🔴 Error saving branch:", error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm("คุณแน่ใจหรือไม่ว่าต้องการลบสาขานี้?")) {
            try {
                await deleteBranch(id);
                fetchBranches();
            } catch (error) {
                console.error("🔴 Error deleting branch:", error);
            }
        }
    };

    return (
        <div className="branch-container">
            <h2>🏢 ระบบจัดการสาขา</h2>
            <div className="d-flex justify-content-between align-items-center mb-3">
                <button className="btn btn-secondary" onClick={() => navigate("/dashboard")}>⬅️ กลับไปหน้า Dashboard</button>
                <button className="btn btn-primary" onClick={() => setShowAddModal(true)}>➕ เพิ่มสาขา</button>
            </div>

            {/* ตารางสาขา */}
            <table className="table table-bordered table-striped">
                <thead className="table-dark">
                    <tr>
                        <th>ชื่อสาขา</th>
                        <th>ที่อยู่</th>
                        <th>เบอร์ติดต่อ</th>
                        <th>จัดการ</th>
                    </tr>
                </thead>
                <tbody>
                    {branches.map(branch => (
                        <tr key={branch.id}>
                            <td>{branch.name}</td>
                            <td>{branch.location}</td>
                            <td>{branch.phone}</td>
                            <td>
                                <button className="btn btn-warning btn-sm" onClick={() => { setEditBranch(branch); setShowAddModal(true); }}>✏️ แก้ไข</button>
                                <button className="btn btn-danger btn-sm ms-2" onClick={() => handleDelete(branch.id)}>❌ ลบ</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* แสดง Modal เมื่อกดปุ่ม "เพิ่มสาขา" หรือ "แก้ไขสาขา" */}
            {showAddModal && (
                <AddBranchModal
                    show={showAddModal}
                    onClose={() => { setShowAddModal(false); setEditBranch(null); }}
                    onSave={handleCreateOrUpdateBranch}
                    branchData={editBranch}
                />
            )}
        </div>
    );
};

export default BrandManagement;
